SELECT POST_ID, POST_TITLE, POST_LOCATION_KEYWORD, POST_REGISTER_DATE, POST_UPDATE_DATE, POST_LOCATION_LAT, POST_LOCATION_LNG FROM ( SELECT @ROWNUM := @ROWNUM + 1 AS ROWNUM, POST_ID, POST_TITLE, POST_LOCATION_KEYWORD, POST_REGISTER_DATE, POST_UPDATE_DATE, POST_LOCATION_LAT, POST_LOCATION_LNG FROM (SELECT DISTINCT(p.POST_ID) POST_ID, POST_TITLE, POST_LOCATION_KEYWORD, POST_REGISTER_DATE, POST_UPDATE_DATE, POST_LOCATION_LAT, POST_LOCATION_LNG FROM POSTS p LEFT JOIN POST_TAGS t ON p.POST_ID=t.POST_ID WHERE p.SPACE_ID=#{spaceId}
AND POST_TITLE LIKE CONCAT('%',#{keyword},'%') OR POST_CONTENT LIKE CONCAT('%',#{keyword},'%')
AND #{date} BETWEEN POST_BEGIN_DATE AND POST_END_DATE
OR #{date} BETWEEN POST_BEGIN_DATE AND POST_END_DATE
AND t.TAG_ID IN
#{tag}
AND USER_ID IN
#{writer}
ORDER BY POST_REGISTER_DATE DESC ) D2
WHERE @ROWNUM:=0 <= #{page} * #{amount} ) D3 WHERE ROWNUM > (#{page} - 1) * #{amount}

SELECT COUNT(POST_ID)
FROM
(
